# Define a function to rank the resumes
def rank_resumes(resume_paths, keywords_dict, ascending=False):
    rankings = []
    for path in resume_paths:
        try:
            # Load the resume file
            with open(path, 'r', encoding='utf-8') as f:
                resume_text = f.read().strip()

            # Skip empty resume files
            if not resume_text:
                continue

            # Parse the resume text using Spacy
            doc = nlp(resume_text.lower())

            # Compute the ranking score for the resume based on the keywords
            ranking_score = sum([doc.text.lower().count(keyword.lower()) * weight for keyword, weight in keywords_dict.items()])

            rankings.append((os.path.basename(path), ranking_score))
        except FileNotFoundError:
            print(f'File not found: {path}')

    # Sort the resumes by their ranking score in ascending or descending order
    rankings = sorted(rankings, key=lambda x: x[1], reverse=not ascending)
    return rankings

