identified_relevant_resumes = set([resume for resume, _ in ranked_resumes])
true_positives = len(relevant_resumes.intersection(identified_relevant_resumes))

precision = true_positives / len(identified_relevant_resumes)
recall = true_positives / len(relevant_resumes)

# Calculate Mean Average Precision (MAP)
average_precisions = []

ranked_resumes = rank_resumes(resume_paths, keywords_dict, ascending=True)

relevant_set = set([resume for resume, relevant in sample_ground_truth if relevant])

average_precision = 0.0
num_relevant = 0

for i, (resume, _) in enumerate(ranked_resumes, start=1):
    if resume in relevant_set:
        num_relevant += 1
        precision = num_relevant / i
        average_precision += precision

if num_relevant > 0:
    average_precision /= num_relevant

average_precisions.append(average_precision)

# Calculate Mean Average Precision (MAP)
map_score = np.mean(average_precisions)

# Display evaluation metrics
print(f"Precision: {precision}")
print(f"Recall: {recall}")
print(f"Execution Time: {execution_time} seconds")
print(f"Mean Average Precision (MAP): {map_score}")

